import { describe, expect, it, test } from "vitest";
import {
  checkIsContainJapanese,
  checkIsMentionNFT,
  checkIsVerifiedAccount,
  hideNonJapaneseTweet,
  hideSpecificAccount,
} from "./hideFlags";
import { getTweet, setTweet } from "../testUtils";

describe("checkIsContainJapanese()", () => {
  test("Given a sentence in English - it should return false", () => {
    expect(
      checkIsContainJapanese(
        "See how IoT and predictive analytics help Trinchero Family Estates boost its sales."
      )
    ).toBe(false);
  });

  test("Given a sentence in Mandarin - it should return false", () => {
    expect(
      checkIsContainJapanese(
        "第19届亚洲运动会周六（9月23日）晚在中国东部城市杭州开幕。"
      )
    ).toBe(false);
  });

  test("Given a sentence in Japanese - it should return true", () => {
    expect(
      checkIsContainJapanese(
        "365日24時間、Yahoo! JAPANトップページに掲出される話題を速報でお届け。"
      )
    ).toBe(true);
  });
});

describe("checkIsMentionNFT()", () => {
  test("Given an account name with a word NFT - it should return true", () => {
    expect(checkIsMentionNFT("ピクミンおぢさん ｜NFTブロガー｜MetaKozo")).toBe(
      true
    );
  });

  test("Given regular account name without a word NFT - it should return false", () => {
    expect(checkIsMentionNFT("🍇ぶどう🍇王子👑")).toBe(false);
  });
});

describe("checkIsVerifiedAccount()", () => {
  test("Given a tweet from verified account - it should return true", () => {
    setTweet(VERIFIED_JAPANESE_TWEET);

    expect(checkIsVerifiedAccount(getTweet())).toBe(true);
  });

  test("Given a tweet from unverified account - it should return faslse", () => {
    setTweet(UNVERIFIED_ACCOUNT_TEET);

    expect(checkIsVerifiedAccount(getTweet())).toBe(false);
  });
});

describe("hideSpecificAccount()", () => {
  test("Case 1 - Given a tweet from NFT related account - it should return true", () => {
    setTweet(NFT_ACCOUNT_TWEET);
    const targetTweet = getTweet();

    expect(hideSpecificAccount(targetTweet)).toBe(true);
  });

  test("Case 2 - Given a tweet from verified non-japanese account - it should return true", () => {
    setTweet(VERIFIED_ENGLISH_ACCOUNT_TWEET);
    const targetTweet = getTweet();

    expect(hideSpecificAccount(targetTweet)).toBe(true);
  });

  test("Case 2 - Given a tweet from verified Japanese account - it should return true", () => {
    setTweet(VERIFIED_JAPANESE_TWEET);
    const targetTweet = getTweet();

    expect(hideSpecificAccount(targetTweet)).toBe(false);
  });
});

test("hideNonJapaneseTweet()", () => {
  test("Given a tweet contains Japanese - it should return false", () => {
    setTweet(VERIFIED_JAPANESE_TWEET);
    const targetTweet = getTweet();

    expect(hideNonJapaneseTweet(targetTweet)).toBe(false);
  });

  test("Given a tweet in English - it should return true", () => {
    setTweet(VERIFIED_ENGLISH_ACCOUNT_TWEET);
    const targetTweet = getTweet();

    expect(hideNonJapaneseTweet(targetTweet)).toBe(true);
  });

  test("Given a tweet in Mandarin - it should return true", () => {
    setTweet(UNVERIFIED_MANDARIN_TWEET);
    const targetTweet = getTweet();

    expect(hideNonJapaneseTweet(targetTweet)).toBe(true);
  });
});

